  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `comments`
--

INSERT INTO `comments` (`id`, `user_id`, `article_id`, `content`, `moderate`, `created_at`, `updated_at`) VALUES
(12, 1, 2, 'Есть над чем задуматься: многие известные личности могут быть разоблачены. Банальные, но неопровержимые выводы, а также явные признаки победы институционализации могут быть разоблачены.', 1, '2020-09-11 16:47:00', '2020-09-17 18:26:51'),
(13, 3, 3, 'Повседневная практика показывает, что внедрение современных методик предоставляет широкие возможности для инновационных методов управления процессами', 0, '2020-09-11 16:49:00', '2020-09-17 16:32:08'),
(15, 2, 2, 'Приятно, граждане, наблюдать, как интерактивные прототипы, вне зависимости от их уровня, должны быть рассмотрены исключительно в разрезе маркетинговых и финансовых предпосылок.', 1, '2020-09-11 17:42:00', '2020-09-17 16:32:11'),
(17, 1, 16, 'Принимая во внимание показатели успешности, перспективное планирование, в своём классическом представлении, допускает внедрение системы массового участия. Разнообразный и богатый опыт говорит нам, что укрепление и развитие внутренней структуры, в своём классическом представлении, допускает внедрение поставленных обществом задач', 1, '2020-09-17 16:35:00', '2020-09-17 16:35:15'),
(18, 1, 15, 'Равным образом, существующая теория, в своём классическом представлении, допускает внедрение экспериментов, поражающих по своей масштабности и грандиозности.', 1, '2020-09-17 16:35:00', '2020-09-17 18:10:48');

